resources.string:
uniform mat4 u_MVPMatrix; // A constant representing the combined model/view/projection matrix.
 attribute vec4 a_Position; // Per-vertex position information we will pass in.
 attribute vec2 a_UV;

 varying vec2 v_UV; // This will be passed into the fragment shader.

 void main() // The entry point for our vertex shader.
 { 
 v_UV = a_UV;
 gl_Position = u_MVPMatrix* a_Position; // gl_Position is a special variable used to store the final position.
 // Multiply the vertex by the matrix to get the final point in
 } // normalized screen coordinates.

varying vec2 v_UV;
 uniform sampler2D myTextureSampler;
 void main() // The entry point for our fragment shader.
 { 
 gl_FragColor = texture2D(myTextureSampler,v_UV);
 }
